(3628, 289, '_position', 'field_65970d76c4123'),
(3629, 289, 'first_col', 'Dr. Naber practices emergency medicine at the University of Cincinnati Health System (UCHS) community hospital in West Chester, Ohio and its Level 1 Trauma Center. He also works part-time in the emergency department at Cincinnati\'s Veterans Administration Medical Center.\r\n\r\nHe is the medical director for utilization review and clinical documentation improvement at West Chester Hospital and the University of Cincinnati (UC) Medical Center.'),
(3630, 289, '_first_col', 'field_65970db9c4124'),
(3631, 289, 'second_col', 'Dr. Naber received his Bachelor of Science from the University of Dayton, Ohio, Doctor of Medicine from the University of Cincinnati, and Juris Doctor from the UC College of Law. He is a member of the Ohio Bar Association and the Cincinnati Bar Association.\r\n\r\nTerm: May 7, 2019 - February 29, 2024'),
(3632, 289, '_second_col', 'field_65970dcec4125'),
(3633, 290, '_edit_last', '1'),
