INSERT INTO `wp_postmeta` (`meta_id`, `post_id`, `meta_key`, `meta_value`) VALUES
(5591, 318, '_flexible_content_6_slider_0_title', 'field_6597303def42b'),
(5592, 318, 'flexible_content_6_slider_0_text', 'The Capability Maturity Model Integration (CMMI®) is an internationally recognized method for evaluating and measuring the maturity of an organization\'s business processes. This certification is appraised at CMMI for Development Level 3 by the CMMI Institute, assuring customers that it has well-defined, effective, and repeatable processes for managing requirements, decision-making, performance measurement, planning, and risk management. Livanta\'s Development maturity is assessed every three years to maintain its Maturity Level 3 appraisal ratings.'),
(5593, 318, '_flexible_content_6_slider_0_text', 'field_6597305eef42f'),
(5594, 318, 'flexible_content_6_slider_0_image', '328'),
(5595, 318, '_flexible_content_6_slider_0_image', 'field_65973072ef430'),
(5596, 318, 'flexible_content_6_slider_1_title', 'ISO 9001:2015 Certified Quality Management System'),
(5597, 318, '_flexible_content_6_slider_1_title', 'field_6597303def42b'),
(5598, 318, 'flexible_content_6_slider_1_text', 'Livanta maintains a comprehensive Quality Management System (QMS) that is certified to the globally recognized International Organization for Standardization (ISO 9001:2015) standard. This certification has strengthened Livanta\'s culture of excellence and assures customers that Livanta is providing a consistent level of quality through well-defined processes and procedures. Organizations must recertify to the standard every three years and undergo annual audits. Certification is based on eight main business principles: customer focus, leadership, involvement of people, process approach, system approach to management, continual improvement, factual approach to decision making, and mutually beneficial supplier relationships.'),
(5599, 318, '_flexible_content_6_slider_1_text', 'field_6597305eef42f'),
(5600, 318, 'flexible_content_6_slider_1_image', '329'),
(5601, 318, '_flexible_content_6_slider_1_image', 'field_65973072ef430'),
(5602, 318, 'flexible_content_6_slider_2_title', 'ANSI National Accreditation Board (ANAB)'),
(5603, 318, '_flexible_content_6_slider_2_title', 'field_6597303def42b'),
