(8135, 389, '_flexible_content_3_dropdown_0_col_left', 'field_65993d7424d9a'),
(8136, 389, 'flexible_content_3_dropdown_0_col_right', 'Value Proposition\r\nWhen evidence-based standards of care support the level of healthcare provision, the outcomes are idealized as the cost of care is reduced.'),
(8137, 389, '_flexible_content_3_dropdown_0_col_right', 'field_65993d7424d9b'),
(8138, 389, 'flexible_content_3_dropdown_1_title', 'Payment Accuracy Review'),
(8139, 389, '_flexible_content_3_dropdown_1_title', 'field_65993d7424d99'),
(8140, 389, 'flexible_content_3_dropdown_1_col_left', 'Livanta’s Payment Accuracy Review services address healthcare claim analytics, coding validation, medical record review, quality of care review, error rate evaluation, and validation of improper payment determinations.'),
(8141, 389, '_flexible_content_3_dropdown_1_col_left', 'field_65993d7424d9a'),
(8142, 389, 'flexible_content_3_dropdown_1_col_right', 'Value Proposition\r\nPayers, providers, and beneficiaries all benefit from properly documented and submitted claims. This means less work for providers, increased savings for payers, and more satisfied beneficiaries.'),
(8143, 389, '_flexible_content_3_dropdown_1_col_right', 'field_65993d7424d9b'),
(8144, 389, 'flexible_content_3_dropdown_2_title', 'Performance, Compliance, And Financial Audit'),
(8145, 389, '_flexible_content_3_dropdown_2_title', 'field_65993d7424d99'),
(8146, 389, 'flexible_content_3_dropdown_2_col_left', 'Livanta’s Performance, Compliance, and Financial Audit services address healthcare across clinical, statistical, accounting, and administrative disciplines. These services ensure healthcare metrics conform to expected standards.'),
(8147, 389, '_flexible_content_3_dropdown_2_col_left', 'field_65993d7424d9a'),
(8148, 389, 'flexible_content_3_dropdown_2_col_right', 'Value Proposition\r\nAgile support and confirmation of evidence-based healthcare that is documented, coded, and claimed properly improves healthcare outcomes.'),
(8149, 389, '_flexible_content_3_dropdown_2_col_right', 'field_65993d7424d9b'),
(8150, 389, 'flexible_content_3_dropdown_3_title', 'Standard of Care and Peer Review'),
(8151, 389, '_flexible_content_3_dropdown_3_title', 'field_65993d7424d99'),
(8152, 389, 'flexible_content_3_dropdown_3_col_left', 'Livanta’s Standard of Care and Peer Review services ensures optimal healthcare provision through successful quality improvement and stakeholder interactions.'),
(8153, 389, '_flexible_content_3_dropdown_3_col_left', 'field_65993d7424d9a'),
(8154, 389, 'flexible_content_3_dropdown_3_col_right', 'Value Proposition\r\nWorking with providers, beneficiaries, and other healthcare stakeholders serves to provide for continual improvement in healthcare outcomes, including optimal health, avoided complications, and reduced cost.'),
(8155, 389, '_flexible_content_3_dropdown_3_col_right', 'field_65993d7424d9b'),
(8156, 389, 'flexible_content_3_dropdown_4_title', 'Error Rate Measurement and Validation'),
(8157, 389, '_flexible_content_3_dropdown_4_title', 'field_65993d7424d99'),
(8158, 389, 'flexible_content_3_dropdown_4_col_left', 'Livanta’s Error Rate Measurement and Validation services offer health plans and related stakeholders practical experience with error rate workloads, including validating measured errors.'),
(8159, 389, '_flexible_content_3_dropdown_4_col_left', 'field_65993d7424d9a'),
(8160, 389, 'flexible_content_3_dropdown_4_col_right', 'Value Proposition\r\nThrough accurate recognition of payment errors, Livanta contributes to the necessary efforts to pay healthcare claims properly.'),
