(8363, 397, '_flexible_content_3_dropdown_0_col_right', 'field_65993d7424d9b'),
(8364, 397, 'flexible_content_3_dropdown_1_title', 'Payment Accuracy Review'),
(8365, 397, '_flexible_content_3_dropdown_1_title', 'field_65993d7424d99'),
(8366, 397, 'flexible_content_3_dropdown_1_col_left', 'Livanta’s Payment Accuracy Review services address healthcare claim analytics, coding validation, medical record review, quality of care review, error rate evaluation, and validation of improper payment determinations.'),
(8367, 397, '_flexible_content_3_dropdown_1_col_left', 'field_65993d7424d9a'),
(8368, 397, 'flexible_content_3_dropdown_1_col_right', 'Value Proposition\r\nPayers, providers, and beneficiaries all benefit from properly documented and submitted claims. This means less work for providers, increased savings for payers, and more satisfied beneficiaries.'),
(8369, 397, '_flexible_content_3_dropdown_1_col_right', 'field_65993d7424d9b'),
(8370, 397, 'flexible_content_3_dropdown_2_title', 'Performance, Compliance, And Financial Audit'),
(8371, 397, '_flexible_content_3_dropdown_2_title', 'field_65993d7424d99'),
(8372, 397, 'flexible_content_3_dropdown_2_col_left', 'Livanta’s Performance, Compliance, and Financial Audit services address healthcare across clinical, statistical, accounting, and administrative disciplines. These services ensure healthcare metrics conform to expected standards.'),
(8373, 397, '_flexible_content_3_dropdown_2_col_left', 'field_65993d7424d9a'),
(8374, 397, 'flexible_content_3_dropdown_2_col_right', 'Value Proposition\r\nAgile support and confirmation of evidence-based healthcare that is documented, coded, and claimed properly improves healthcare outcomes.'),
(8375, 397, '_flexible_content_3_dropdown_2_col_right', 'field_65993d7424d9b'),
