(8379, 397, '_flexible_content_3_dropdown_3_col_left', 'field_65993d7424d9a'),
(8380, 397, 'flexible_content_3_dropdown_3_col_right', 'Value Proposition\r\nWorking with providers, beneficiaries, and other healthcare stakeholders serves to provide for continual improvement in healthcare outcomes, including optimal health, avoided complications, and reduced cost.'),
(8381, 397, '_flexible_content_3_dropdown_3_col_right', 'field_65993d7424d9b'),
