(8605, 398, '_flexible_content_3_dropdown_1_title', 'field_65993d7424d99'),
(8606, 398, 'flexible_content_3_dropdown_1_col_left', 'Livanta’s Payment Accuracy Review services address healthcare claim analytics, coding validation, medical record review, quality of care review, error rate evaluation, and validation of improper payment determinations.'),
(8607, 398, '_flexible_content_3_dropdown_1_col_left', 'field_65993d7424d9a'),
(8608, 398, 'flexible_content_3_dropdown_1_col_right', 'Value Proposition\r\nPayers, providers, and beneficiaries all benefit from properly documented and submitted claims. This means less work for providers, increased savings for payers, and more satisfied beneficiaries.'),
(8609, 398, '_flexible_content_3_dropdown_1_col_right', 'field_65993d7424d9b'),
(8610, 398, 'flexible_content_3_dropdown_2_title', 'Performance, Compliance, And Financial Audit'),
(8611, 398, '_flexible_content_3_dropdown_2_title', 'field_65993d7424d99'),
(8612, 398, 'flexible_content_3_dropdown_2_col_left', 'Livanta’s Performance, Compliance, and Financial Audit services address healthcare across clinical, statistical, accounting, and administrative disciplines. These services ensure healthcare metrics conform to expected standards.'),
(8613, 398, '_flexible_content_3_dropdown_2_col_left', 'field_65993d7424d9a'),
(8614, 398, 'flexible_content_3_dropdown_2_col_right', 'Value Proposition\r\nAgile support and confirmation of evidence-based healthcare that is documented, coded, and claimed properly improves healthcare outcomes.'),
(8615, 398, '_flexible_content_3_dropdown_2_col_right', 'field_65993d7424d9b'),
(8616, 398, 'flexible_content_3_dropdown_3_title', 'Standard of Care and Peer Review'),
(8617, 398, '_flexible_content_3_dropdown_3_title', 'field_65993d7424d99'),
(8618, 398, 'flexible_content_3_dropdown_3_col_left', 'Livanta’s Standard of Care and Peer Review services ensures optimal healthcare provision through successful quality improvement and stakeholder interactions.'),
(8619, 398, '_flexible_content_3_dropdown_3_col_left', 'field_65993d7424d9a'),
(8620, 398, 'flexible_content_3_dropdown_3_col_right', 'Value Proposition\r\nWorking with providers, beneficiaries, and other healthcare stakeholders serves to provide for continual improvement in healthcare outcomes, including optimal health, avoided complications, and reduced cost.'),
(8621, 398, '_flexible_content_3_dropdown_3_col_right', 'field_65993d7424d9b'),
