(10805, 408, 'flexible_content_5_image_mob', '388'),
(10806, 408, '_flexible_content_5_image_mob', 'field_65994825e00ab'),
(10807, 408, 'flexible_content_5_dropdown_0_title', 'Focused Reviews'),
(10808, 408, '_flexible_content_5_dropdown_0_title', 'field_659946c8a3568'),
(10809, 408, 'flexible_content_5_dropdown_0_col_left', 'Livanta develops strategies for special studies and conducts complex medical reviews of selected sampled claims and medical records. The result is a high standard of effective and accurate evaluation of healthcare claim reporting.'),
(10810, 408, '_flexible_content_5_dropdown_0_col_left', 'field_659946c8a3569'),
(10811, 408, 'flexible_content_5_dropdown_0_col_right', 'Value Proposition\r\nFocused Review processes support confirmation and correction of healthcare quality of outcomes.'),
(10812, 408, '_flexible_content_5_dropdown_0_col_right', 'field_659946c8a356a'),
(10813, 408, 'flexible_content_5_dropdown_1_title', 'Special Measures'),
(10814, 408, '_flexible_content_5_dropdown_1_title', 'field_659946c8a3568'),
(10815, 408, 'flexible_content_5_dropdown_1_col_left', 'Under Medicare leadership, Livanta proposes and completes special measures based on confirmed quality concerns. Using sound data analytics, Livanta promotes understanding and correction of these concerns.'),
(10816, 408, '_flexible_content_5_dropdown_1_col_left', 'field_659946c8a3569'),
(10817, 408, 'flexible_content_5_dropdown_1_col_right', 'Value Proposition\r\nLivanta contributes to improved healthcare outcomes and cost reductions by identifying errors and promoting solutions.'),
(10818, 408, '_flexible_content_5_dropdown_1_col_right', 'field_659946c8a356a'),
(10819, 408, 'flexible_content_5_dropdown_2_title', 'Topic Trends'),
(10820, 408, '_flexible_content_5_dropdown_2_title', 'field_659946c8a3568'),
(10821, 408, 'flexible_content_5_dropdown_2_col_left', 'Livanta’s Topic Trends services provides broad metrics of population health data (such as social determinants of health in COVID-19) helping to identify emerging or increasing strain on the population and elucidating new patterns to positively impact population health.'),
(10822, 408, '_flexible_content_5_dropdown_2_col_left', 'field_659946c8a3569'),
(10823, 408, 'flexible_content_5_dropdown_2_col_right', 'Value Proposition\r\nConnecting healthcare population trends with root data evaluation promotes awareness, analysis and correction of a given healthcare concern.'),
(10824, 408, '_flexible_content_5_dropdown_2_col_right', 'field_659946c8a356a'),
