(11585, 492, '_flexible_content_2_list_1_icon', 'field_658f1193e07ac'),
(11586, 492, 'flexible_content_2_list_1_desc', 'Livanta’s experienced clinical staff includes physicians, nurses, social workers, and other healthcare professionals.'),
