(11597, 492, '_flexible_content_3_title', 'field_658f1f306fc16'),
(11598, 492, 'flexible_content_3_text', 'We assist healthcare providers, helping them improve health outcomes, address patients’ needs, and strengthen their businesses.\r\n\r\nThrough contracts with government agencies, Livanta provides quality oversight and auditing of healthcare organizations and provides education to the provider community to improve the quality of healthcare delivery.'),
(11599, 492, '_flexible_content_3_text', 'field_658f1f406fc17'),
