(11603, 492, '_flexible_content_3_list_0_icon', 'field_658f1f746fc1b'),
(11604, 492, 'flexible_content_3_list_0_desc', 'Livanta’s valued and distinctive team of professionals is highly respected by the healthcare industry for its expertise in revenue integrity in appeals and denials management, coding, and employee training programs.'),
(11605, 492, '_flexible_content_3_list_0_desc', 'field_658f1f826fc1c'),
