(11607, 492, '_flexible_content_3_list_1_icon', 'field_658f1f746fc1b'),
(11608, 492, 'flexible_content_3_list_1_desc', 'Livanta’s staff are proficient in monitoring inpatient and outpatient costs by performing administrative data validation and coding accuracy services.'),
(11609, 492, '_flexible_content_3_list_1_desc', 'field_658f1f826fc1c'),
