(11631, 492, '_flexible_content_4_list_0_icon', 'field_658f2e6c21bbd'),
(11632, 492, 'flexible_content_4_list_0_desc', 'Livanta supports quality cost-effective healthcare by performing reviews of claims cost accuracy, payer appeals, coordinating care during patient transitions of care, and reducing hospital readmissions through advocacy services.'),
(11633, 492, '_flexible_content_4_list_0_desc', 'field_658f2e7c21bbe'),
